(function ($) {
  Drupal.behaviors.elifeAnchorJump = {
    attach: function (context, settings) {
      // Hijack anchor links to make sure they deal with the fixed header
      $('a.xref-table, a.xref-bibr, a.xref-supplementary-material, a.xref-table-fn, .elife-fig-data-title-jump-link a', context).once('elifeAnchorJump', function() {
        $(this).click(function(){
          var $target = $($(this).attr('href'));
          if ($target.length > 0) {
            $(window).scrollTop($target.offset().top - 43);
            return false;
          }
          else {
            // The link doesn't exist in the full text, send the user to a new page that should have it
            window.open(window.location.pathname + $(this).attr('href').replace('#','/') ,'_blank');
            return false;
          }
        });
      });
      
      // Open all external links in a new window
      $("a[href^='http:']", context).not("[href*='"+ document.domain + "']").attr('target','_blank');
    }
  };
})(jQuery);
;

(function ($) {
  Drupal.Panels = Drupal.Panels || {};

  Drupal.Panels.autoAttach = function() {
    if ($.browser.msie) {
      // If IE, attach a hover event so we can see our admin links.
      $("div.panel-pane").hover(
        function() {
          $('div.panel-hide', this).addClass("panel-hide-hover"); return true;
        },
        function() {
          $('div.panel-hide', this).removeClass("panel-hide-hover"); return true;
        }
      );
      $("div.admin-links").hover(
        function() {
          $(this).addClass("admin-links-hover"); return true;
        },
        function(){
          $(this).removeClass("admin-links-hover"); return true;
        }
      );
    }
  };

  $(Drupal.Panels.autoAttach);
})(jQuery);
